targets.query:
from(bucket: "ups")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "ups_data")
  |> filter(fn: (r) => r["_field"] == "upsEBatteryEstimatedChargeRemaining" ) 
  |> map(fn: (r) => ({
      _time: r._time,
      _value: 
        if r["_field"] == "upsEBatteryEstimatedChargeRemaining" then float(v: r._value) 
        else float(v: r._value),
      _field: r._field,
      _measurement: r._measurement
  }))
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> yield(name: "mean")
